//VERSION=3
// This custom script visualises LCFM - Tree Cover Density MAP layer
function setup() {
  return {
    input: ["TCD10", "dataMask"],
    output: {
      bands: 4,
      sampleType: "AUTO",
    },
  };
}
const map = [
  [10, 0xf0f0f0], // >=0% & <=10%
  [20, 0xfdff73], // >10% & <=20%
  [30, 0xb7ff73], // >20% & <=30%
  [40, 0x94fa5c], // >30% & <=40%
  [50, 0x71f02e], // >40% & <=50%
  [60, 0x4ce600], // >50% & <=60%
  [70, 0x42c724], // >60% & <=70%
  [80, 0x41ab3e], // >70% & <=80%
  [90, 0x3d9143], // >80% & <=90%
  [100, 0x2b7533], // >90% & <=100%
  [254, 0x9c9c9c], // unclassifiable
];
const visualizer = new ColorMapVisualizer(map);
function evaluatePixel(sample) {
  return visualizer.process(sample.TCD10).concat(sample.dataMask);
}

//VERSION=3
const factor = 1 / 50; // EDIT FACTOR
const offset = 0; // EDIT OFFSET

function setup() {
  return {
    // EDIT VARIABLE NAME
    input: ["GDMP", "dataMask"],
    output: [
      { id: "default", bands: 4, sampleType: "UINT8" },
      { id: "index", bands: 1, sampleType: "FLOAT32" },
      { id: "eobrowserStats", bands: 2, sampleType: "FLOAT32" },
      { id: "dataMask", bands: 1 },
    ],
  };
}

function evaluatePixel(samples) {
  // EDIT VARIABLE NAME
  var originalValue = samples.GDMP;

  let val = originalValue * factor + offset;

  let dataMask = samples.dataMask;

  const indexVal = dataMask === 1 ? val : NaN;
  const imgVals = visualizer.process(val);

  return {
    default: imgVals.concat(dataMask * 255),
    index: [indexVal],
    eobrowserStats: [val, dataMask],
    dataMask: [dataMask],
  };
}

// EDIT COLOR BAR
const ColorBar = [
  [0.0, [255, 255, 255]],
  [0.02, [255, 0, 0]],
  [60.0, [255, 165, 0]],
  [120.0, [255, 215, 0]],
  [180.0, [255, 255, 0]],
  [240.0, [173, 255, 47]],
  [300.0, [20, 255, 20]],
  [360.0, [10, 200, 10]],
  [420.0, [0, 136, 0]],
];
const visualizer = new ColorRampVisualizer(ColorBar);

