//VERSION=3
function setup() {
  return {
    input: ["HH", "HV", "dataMask"],
    output: { bands: 4 }
  }
}

function evaluatePixel(sample) {
  var water_threshold = 25; //lower means more water

  if (sample.HH / sample.HV > water_threshold) {
    // watervis
    return [sample.HH, 8 * sample.HH, 0.5 + 3 * sample.HH + 2000 * sample.HV, sample.dataMask];
  } else {
    // landvis
    return [3 * sample.HH, 1.1 * sample.HH + 8.75 * sample.HV, 1.75 * sample.HV, sample.dataMask];
  }
}

