//VERSION=3
let minVal = 0.0;
let maxVal = 1.0;

let viz = new DefaultVisualizer(minVal, maxVal);

function evaluatePixel(samples) {
    let val = samples.GIFAPAR;
    val = viz.process(val);
    return [val, samples.dataMask];
}

function setup() {
  return {
    input: [{
      bands: [
        "GIFAPAR",
        "dataMask"
      ]
    }],
    output: {
      bands: 2
    }
  }
}

//VERSION=3

let rangeMin = 1.0;
let rangeMax = 70.0;
let viz = ColorRampVisualizer.createOceanColor(rangeMin, rangeMax);

function setup() {
  return {
    input: [{
      bands: ["IWV_W", "dataMask"]
    }],
    output: { bands: 4 }
  }
}

function evaluatePixel(samples) {
  if (samples.dataMask === 0) {
    return [0, 0, 0, samples.dataMask];
  }
  return [...viz.process(samples.IWV_W), samples.dataMask];
}

