//VERSION=3 
const map = [
    [0.0, 0x00007d],
    [0.8, 0x004ccc],
    [1.44, 0xff3333],
    [2.0, 0xffe500],
    [3.2, 0x00cc19],
    [3.6, 0x00cc19],
    [4.0, 0xffffff]
];

const visualizer = new ColorRampVisualizer(map);
function setup() {
    return {
        input: ["OTCI", "dataMask"],
        output: [{ id: "default", bands: 4 }]
    };
}

function evaluatePixel(samples) {
    return { default: [...visualizer.process(samples.OTCI), samples.dataMask] };
}

